job.command:
        starver SL16d_embed

        echo OOO OOO OOO OOO OOO OOO OOO OOO b0
        set log = "&outname;.log"
        echo OOO OOO OOO OOO OOO OOO OOO OOO b1
        echo logname ${log}
        echo OOO OOO OOO OOO OOO OOO OOO OOO b2



        echo OOO OOO OOO OOO OOO OOO OOO OOO a1
        echo "Building input list from location:" > ${log}
        echo "Input files from:" >> ${log}
        echo &dir; >> ${log}
        echo "-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-" >> ${log}
        echo " " >> ${log}
        echo "List of *MuDst.root copied files" >> ${log}
        echo "(note that there are also *.minimc.root copied files)" >> ${log}
        echo "-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-" >> ${log}
        echo OOO OOO OOO OOO OOO OOO OOO OOO a2

        set events_list=events.list
        mkdir events
        echo OOO OOO OOO OOO OOO OOO OOO OOO a3
        touch events/${events_list}

        echo OOO OOO OOO OOO OOO OOO OOO OOO a4
        set i = 0
        foreach file ( `find &dir; -name "*.MuDst.root"` )
            set i = `expr ${i} + 1`
            set base = `basename -s .MuDst.root ${file}`
            set tree = `dirname ${file}`
            echo ${file} >> ${log}
            cp ${tree}/${base}.MuDst.root  ./events/${base}_${i}.MuDst.root
            cp ${tree}/${base}.minimc.root ./events/${base}_${i}.minimc.root
            echo ./events/${base}_${i}.MuDst.root >> ${events_list}
        end
        echo OOO OOO OOO OOO OOO OOO OOO OOO a5
        echo "-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-" >> ${log}
        echo " end of copied files " >> ${log}
        echo " starting extractor TrackEmbedding.C program" >> ${log}

        echo OOO OOO OOO OOO OOO OOO OOO OOO a6
		echo OOO ls locally
        ls events



        echo end ls locally OOO OOO OOO OOO OOO OOO OOO a7
        root4star -q -b trackEmbedding.C\(-1,\"${events_list}\",\"&outname;\"\)
        echo OOO OOO OOO OOO OOO OOO OOO OOO a9
